import { UserService } from 'src/app/services/user.service';
import { Router } from '@angular/router';
import { MenuService } from './../../../services/menu.service';
import { Component } from '@angular/core';
import { BreakpointObserver, Breakpoints } from '@angular/cdk/layout';
import { Observable } from 'rxjs';
import { map, shareReplay } from 'rxjs/operators';
import { Menu } from 'src/app/interface/menu';
import { SiblingService } from 'src/app/services/sibling.service';
import { ToastrService } from 'ngx-toastr';
import { ErrorToastrService } from 'src/app/services/error-toastr.service';
import { MatDialog } from '@angular/material/dialog';
import { AccountComponent } from '../account/account.component';

@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.css', './navigation.component.scss']
})
export class NavigationComponent {

  menu: Menu[] = [];
  show = true;
  role = ""
  isHandset$: Observable<boolean> = this.breakpointObserver.observe(Breakpoints.Handset)
    .pipe(
      map(result => result.matches),
      shareReplay()
    );
  ngOnInit(): void {
    this.listMenu();
    let roleD = localStorage.getItem('role')
    if (roleD != null){
      this.role = roleD
    }
    // if (this.role != "admin") {
    //   this.menu[2].show = false;
    // }
  }
  constructor(private toastr: ToastrService,public dialog: MatDialog, private sibling: SiblingService, private errToastr: ErrorToastrService, private user: UserService, private breakpointObserver: BreakpointObserver, private _menuServices: MenuService, private sibServices: SiblingService, private router: Router) { }
  ngAfterViewInit(){
    if (this.role != "admin") {
      // this.menu[2].show = false;
    }
  }
  accounts(){
    const dialogRef = this.dialog.open(AccountComponent, {
      width: '700px',
      height: '550px',
    });
    dialogRef.afterClosed().subscribe(() => {

    });
  }
  listMenu() {
    this._menuServices.getMenu().subscribe(data => {
      this.menu = data;
      if (this.role != "admin") {
        this.menu[2].show = false;
      }
    },
    )
  }
  logout() {
    this.user.logoutUser().subscribe((res: any) => {
      if (res.mess == "Done") {
        this.toastr.success('Logout Successful', 'Done');
      }
    }),
      (err: any) => {
        this.errToastr.errToastr(err);
      }
  }


}
<mat-sidenav-container class="sidenav-container">
  <mat-sidenav
    #drawer
    class="sidenav"
    fixedInViewport
    [attr.role]="(isHandset$ | async) ? 'dialog' : 'navigation'"
    [mode]="(isHandset$ | async) ? 'over' : 'side'"
    [opened]="(isHandset$ | async) === false"
  >
    <mat-toolbar>Menu</mat-toolbar>
    <mat-nav-list>
      <!-- <mat-list-item *ngFor="let item of menu" [routerLink]="item.redirect" [routerLinkActive]="['active']">
        <mat-icon  matListIcon class="nav-list-icon">{{ item.icon }}</mat-icon>
        <a matLine>{{ item.nameMenu }}</a>
      </mat-list-item> -->
      <div *ngFor="let item of menu">
        <a
          *ngIf="item.show"
          mat-list-item
          [routerLink]="item.redirect"
          routerLinkActive
          #routerLinkActiveInstance="routerLinkActive"
          [attr.tabindex]="routerLinkActiveInstance.isActive ? 0 : -1"
          [class.list-item-active]="routerLinkActiveInstance.isActive"
        >
          <mat-icon matListIcon class="nav-list-icon">{{
            item.icon
          }}</mat-icon>
          {{ item.nameMenu }}</a
        >
      </div>
      <!-- <a
        *ngIf="show"
        mat-list-item
        [routerLink]="/dashboard/newserver"
        routerLinkActive
        #routerLinkActiveInstance="routerLinkActive"
        [attr.tabindex]="routerLinkActiveInstance.isActive ? 0 : -1"
        [class.list-item-active]="routerLinkActiveInstance.isActive"
      >
        <mat-icon matListIcon class="nav-list-icon">supervisor_account</mat-icon>
        User</a
      > -->
    </mat-nav-list>
  </mat-sidenav>
  <mat-sidenav-content>
    <mat-toolbar color="primary">
      <button
        type="button"
        aria-label="Toggle sidenav"
        mat-icon-button
        (click)="drawer.toggle()"
        *ngIf="isHandset$"
      >
        <mat-icon aria-label="Side nav toggle icon">menu</mat-icon>
      </button>
      <span>Admin</span>
      <span class="example-spacer"></span>
      <button mat-icon-button class="example-icon favorite-icon account" (click) = "accounts()">
        <mat-icon>manage_accounts</mat-icon>
        <span>Account</span>
      </button>
      <button
        [routerLink]="['/login']"
        routerLinkActive="router-link-active"
        mat-icon-button
        class="example-icon logout"
        (click)="logout()"
      >
        <mat-icon>logout</mat-icon>
        <span>Logout</span>
      </button>
    </mat-toolbar>
    <!-- Add Content Here -->
    <router-outlet></router-outlet>
  </mat-sidenav-content>
</mat-sidenav-container>
